        }

        private void checkTerms()
        {
            String positive = "";
            int count = 0;
            for (int x = 0; x < terms.Length; x++)
            {
                
                if (browser.DocumentText.Split().Contains(terms[x], StringComparer.CurrentCultureIgnoreCase))
                {
                    count++;
                    positive += terms[x]+"\n";
                }
            }
            if (count <= 0)
            {
                MessageBox.Show("This page is safe!\n" + positive);

            }
            else
                MessageBox.Show("This page contains " + count + " prohibited terms:\n" + positive);
            }

     

        private Boolean passURL(String text)
        {

            string newText = "";
            if (!(text.Contains(".com") || text.Contains(".org") || text.Contains(".net"))|| text.Contains(' '))
            {
                if (text.Contains(' '))
                {
                    string[] words = navBar.Text.Split(' ');

                    for (int x = 0; x < words.Length; x++)
                    {
                        newText += words[x] + '+';
                    }
                }
                
                browser.Navigate(search[set] + newText.TrimEnd('+'));
                
                return false;
            }
            return true;

        }


